package Operators;

public class Operators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//math
		// +-/*
		// modulo %
		int  myInt = 1;
		System.out.println(myInt);
		myInt++;
		System.out.println(myInt);
		myInt--;
		System.out.println(myInt);
		myInt += 3;
		System.out.println(myInt);
		myInt *= 3;
		System.out.println(myInt);
		myInt /= 3;
		System.out.println(myInt);
		myInt -= 3;
		System.out.println(myInt);
		// ++ = increment by 1
		// -- = down by 1
		//conditionals
		// == != || && <= >= < >
		// same  ternary as ruby

	}

}
